import { ChangeEvent, DragEvent, useEffect, useRef, useState } from "react";
import tus from "tus-js-client";
import "./Dropzone.css";
import LoadingBar from "./LoadingBar";

interface UploadableFile {
  file: File;
  progress: number;
  complete: boolean;
  error: boolean;
  url: string;
}

interface FileBundle {
  files: Array<UploadableFile | null>;
  custodian: string;
}

const Dropzone = () => {
  const [fileBundles, setFileBundles] = useState<Array<FileBundle | undefined>>(
    []
  );
  const [fileUrls, setFileUrls] = useState<Array<string>>([]);
  const [isHovering, setIsHovering] = useState<boolean>();
  const hiddenInputRef = useRef<HTMLInputElement>(null);

  function addFiles(files: File[]) {
    const uploadableFiles = files.map((file) => {
      const uploadableFile: UploadableFile = {
        file,
        progress: 0,
        complete: false,
        error: false,
        url: "",
      };

      let upload = new tus.Upload(file, {
        endpoint: "https://tusd.tusdemo.net/files/",
        retryDelays: [0, 3000, 5000, 10000, 20000],
        metadata: {
          filename: file?.name,
          filetype: file?.type,
        },
        onError: function (error) {
          console.log("Failed because: " + error);
        },
        onProgress: function (bytesUploaded, bytesTotal) {
          var percentage = ((bytesUploaded / bytesTotal) * 100).toFixed(2);
          console.log(bytesUploaded, bytesTotal, percentage + "%");
        },
        onSuccess: function () {
          console.log("Download %s from %s", upload.file.name, upload.url);
        },
      });

      return uploadableFile;
    });
  }

  function handleDragOver(event: DragEvent<HTMLElement>): void {
    event.preventDefault();
  }

  function handleDrop(event: DragEvent<HTMLElement>): void {
    setIsHovering(false);
    event.preventDefault();
    event.stopPropagation();

    const { items, files } = event.dataTransfer;

    if (items) {
      addFiles(
        Array.from(items)
          .filter((item) => item.kind === "file")
          .map((i) => i.getAsFile())
      );
    } else {
      addFiles(Array.from(files));
    }
  }

  function handleDropzoneClick() {
    hiddenInputRef.current?.click();
  }

  function handleInputChange(event: ChangeEvent<HTMLInputElement>) {
    addFiles(Array.from(event.currentTarget.files || []));
  }

  return (
    <div
      onDragOver={handleDragOver}
      onDragEnter={() => setIsHovering(true)}
      onDragExit={() => setIsHovering(false)}
      onDrop={handleDrop}
      onClick={handleDropzoneClick}
      className={`Dropzone ${isHovering ? "hovering" : ""}`}
    >
      <input
        type="file"
        multiple
        tabIndex={-1}
        className="hiddenInput"
        onChange={handleInputChange}
        ref={hiddenInputRef}
      />
      {fileBundles.length ? (
        <>
          <div className="Dropzone-Bundles">
            {fileBundles.map((bundle, index) => (
              <div className="Dropzone-ListContainer" key={index}>
                <label>Custodian: </label>
                <input
                  onClick={(event) => event.stopPropagation()}
                  type="text"
                  defaultValue={bundle?.custodian}
                />
                <div className="Dropzone-List">
                  {bundle?.files.map((file, index1) => (
                    <div className="Dropzone-ListItem" key={index1}>
                      <img
                        height={18}
                        src="https://cdn-icons-png.flaticon.com/512/633/633585.png"
                      />
                      <span className="Dropzone-ListItem-FileName">
                        {file?.file.name}
                      </span>
                      <LoadingBar percentage={80} />
                    </div>
                  ))}
                </div>
              </div>
            ))}
          </div>
          <div className="Dropzone-SubmitButton">
            <button>Submit</button>
          </div>
        </>
      ) : (
        <p>Drop your files here or click to upload them.</p>
      )}
    </div>
  );
};

export default Dropzone;
