import React, {
    ChangeEvent,
    DragEvent,
    MouseEvent, useRef,
    useState
} from "react";
import "./Dropzone.css";

interface FileBundle {
  files: Array<File | null>;
  custodian: string;
}

const Dropzone = () => {
  const [fileBundles, setFileBundles] = useState<Array<FileBundle | undefined>>(
    []
  );
  const [isHovering, setIsHovering] = useState<boolean>();
  const hiddenInputRef = useRef<HTMLInputElement>(null);

  function addFiles(files: Array<File | null>) {
    setFileBundles((f) => [
      ...f,
      {
        files: files,
        custodian: "Anonymous",
      },
    ]);
  }

  function handleDragOver(event: DragEvent<HTMLElement>): void {
    event.preventDefault();
  }

  function handleDrop(event: DragEvent<HTMLElement>): void {
    setIsHovering(false);
    event.preventDefault();
    event.stopPropagation();

    const { items, files } = event.dataTransfer;

    if (items) {
      addFiles(
        Array.from(items)
          .filter((item) => item.kind === "file")
          .map((i) => i.getAsFile())
      );
    } else {
      addFiles(Array.from(files));
    }
  }

  function handleDropzoneClick(event: MouseEvent<HTMLElement>) {
    hiddenInputRef.current?.click();
  }

  function handleInputChange(event: ChangeEvent<HTMLInputElement>) {
    addFiles(Array.from(event.currentTarget.files || []));
    event.currentTarget.files = null;
  }

  return (
    <div
      onDragOver={handleDragOver}
      onDragEnter={() => setIsHovering(true)}
      onDragExit={() => setIsHovering(false)}
      onDrop={handleDrop}
      onClick={handleDropzoneClick}
      className={`Dropzone ${isHovering ? "hovering" : ""}`}
    >
      <input
        type="file"
        multiple
        tabIndex={-1}
        className="hiddenInput"
        onChange={handleInputChange}
        ref={hiddenInputRef}
      />
      {fileBundles.length ? null : (
        <p>Drop your files here or click to upload them.</p>
      )}
    </div>
  );
};

export default Dropzone;
