import React, { useState } from "react";
import "./Dropzone.css";

interface File {
  pathname: string;
  data: ArrayBuffer;
}

const Dropzone = () => {
  const [files, setFiles] = useState<Array<File | undefined>>([]);

  function handleDragOver(event: React.DragEvent<HTMLElement>): void {
    event.preventDefault();
  }

  function handleDragEnter(event: React.DragEvent<HTMLElement>): void {
    event.preventDefault();
    event.stopPropagation();
    console.log(event);
  }

  function handleDrop(event: React.DragEvent<HTMLElement>): void {
    console.log(event);
    event.preventDefault();
    event.stopPropagation();
  }

  return (
    <div onDragOver={handleDragOver} onDrop={handleDrop} className="Dropzone">
      {files.length ? null : (
        <p>Drop your files here or click to upload them.</p>
      )}
    </div>
  );
};

export default Dropzone;
