import { DragEvent, useLayoutEffect, useRef, useState } from "react";
import "./Dropzone.css";

interface File {
  pathname: string;
  data: ArrayBuffer;
}

const Dropzone = () => {
  const [files, setFiles] = useState<Array<File | undefined>>([]);
  const dropzoneRef = useRef<HTMLDivElement>(null);

  useLayoutEffect(() => {
      console.log(dropzoneRef)
  })

  function handleDragOver(event: DragEvent<HTMLElement>): void {
    event.preventDefault();
  }

  function handleDragEnter(event: DragEvent<HTMLElement>): void {}

  function handleDragExit(event: DragEvent<HTMLElement>): void {
    console.log(event);
  }

  function handleDrop(event: DragEvent<HTMLElement>): void {
    console.log(event);
    event.preventDefault();
    event.stopPropagation();
  }

  return (
    <div
      ref={dropzoneRef}
      onDragOver={handleDragOver}
      onDragEnter={handleDragEnter}
      onDragExit={handleDragExit}
      onDrop={handleDrop}
      className="Dropzone"
    >
      {files.length ? null : (
        <p>Drop your files here or click to upload them.</p>
      )}
    </div>
  );
};

export default Dropzone;
