import { DragEvent, useState } from "react";
import "./Dropzone.css";

const Dropzone = () => {
  const [files, setFiles] = useState<Array<File | undefined>>([]);
  const [isHovering, setIsHovering] = useState<boolean>();

  function handleDragOver(event: DragEvent<HTMLElement>): void {
    event.preventDefault();
  }

  function handleDrop(event: DragEvent<HTMLElement>): void {
    setIsHovering(false);
    event.preventDefault();
    event.stopPropagation();

    const newFiles: Array<File | null> = [];

    const { items, files } = event.dataTransfer;

    if (items) {
      // Use a interface DataTransferItemList para acessar o (s) arquivo (s)
      for (let i = 0, item; (item = items[i]), i < items.length; i++) {
        // Se os itens soltos não forem arquivos, rejeite-os
        if (item.kind === "file") {
          newFiles.push(item.getAsFile());
        }
      }
    } else {
      // Use a interface DataTransfer para acessar o (s) arquivo (s)
      for (var i = 0; i < event.dataTransfer.files.length; i++) {
        console.log(
          "... file[" + i + "].name = " + event.dataTransfer.files[i].name
        );
      }
    }
  }

  return (
    <div
      onDragOver={handleDragOver}
      onDragEnter={() => setIsHovering(true)}
      onDragExit={() => setIsHovering(false)}
      onDrop={handleDrop}
      className={`Dropzone ${isHovering ? "hovering" : ""}`}
    >
      {files.length ? null : (
        <p>Drop your files here or click to upload them.</p>
      )}
    </div>
  );
};

export default Dropzone;
