import React, { useState } from "react";
import "./Dropzone.css";

interface File {
  pathname: string;
  data: ArrayBuffer;
}

const Dropzone = () => {
  const [files, setFiles] = useState<Array<File | undefined>>([]);

  function handleDragEnter(event: React.DragEvent<HTMLElement>): void {
    event.preventDefault();
    event.stopPropagation();
  }

  return (
    <div
      draggable
      onDragEnter={handleDragEnter}
      onDrop={console.log}
      className="Dropzone"
    >
      {files.length ? null : (
        <p>Drop your files here or click to upload them.</p>
      )}
    </div>
  );
};

export default Dropzone;
